import React, { useState } from 'react';
import axios from 'axios';

const CourseForm = ({ onFormClose }) => {
  const [courseData, setCourseData] = useState({
    title: '',
    image: '', // You'll need to handle image uploads here
    price: '',
    // Add other fields as needed
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCourseData({ ...courseData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send a POST request to create a new course
      await axios.post('http://localhost:5000/courses', courseData);
      // Close the form and trigger a refresh of the course list
      onFormClose();
    } catch (error) {
      console.error('Error creating course:', error);
    }
  };

  return (
    <div>
      <h2>Add New Course</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" name="title" value={courseData.title} onChange={handleChange} required />
        </label>
        <label>
          Image:
          <input type="text" name="image" value={courseData.image} onChange={handleChange} required />
        </label>
        <label>
          Price:
          <input type="text" name="price" value={courseData.price} onChange={handleChange} required />
        </label>
        {/* Add additional input fields for other information */}
        <button type="submit">Create Course</button>
      </form>
    </div>
  );
};

export default CourseForm;
