// frontend/LandingPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const LandingPage = () => {
    const [courses, setCourses] = useState([]);
    const [message, setMessage] = useState('');
    const [contactForm, setContactForm] = useState({
        name: '',
        email: '',
        message: ''
    });

    useEffect(() => {
        fetchCourses();
    }, []);

    const fetchCourses = async () => {
        const result = await axios.get('http://localhost:5000/courses');
        setCourses(result.data);
    };

    const register = async () => {
        await axios.post('http://localhost:5000/register', { message });
        setMessage('');
    };
    const handleContactChange = (e) => {
        const { name, value } = e.target;
        setContactForm(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleContactSubmit = (e) => {
        e.preventDefault();
        // Add logic to handle the submission of the contact form
        console.log('Contact form submitted:', contactForm);
        // You can send the contact form data to your backend here
    };

    return (
        
        <div>
            <div>
            <h1>Welcome to The Sridge</h1>
            </div>
            <div>
            <h2>Improve your skills on your own <br /> To prepare for a better future</h2>
            <button onClick={register}>REGISTER NOW</button>
            </div>
            <div>
            <h2>Discover our courses</h2>
            <button>View More</button>
             <ul>
                {courses.map(course => (
                    <li key={course.id}>{course.title}</li>
                ))}
            </ul>
            </div>

             {/* Contact Us Section */}
             <h2>Contact Us</h2>
            <form onSubmit={handleContactSubmit}>
                <label htmlFor="name">Name</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={contactForm.name}
                    onChange={handleContactChange}
                    placeholder="Enter your name"
                />

                <label htmlFor="email">Email</label>
                <input
                    type="email"
                    id="email"
                    name="email"
                    value={contactForm.email}
                    onChange={handleContactChange}
                    placeholder="Enter your email"
                />

                <label htmlFor="message">Message</label>
                <textarea
                    id="message"
                    name="message"
                    value={contactForm.message}
                    onChange={handleContactChange}
                    placeholder="Type your message here"
                />

                <button type="submit">Send The Message</button>
            </form>
        </div>
    );
};

export default LandingPage;
