// src/components/CourseForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CourseForm = ({ onFormClose, selectedCourse }) => {
  const [title, setTitle] = useState('');
  const [price, setPrice] = useState('');
  const [image, setImage] = useState('');
  const [isUpdating, setIsUpdating] = useState(false);

  useEffect(() => {
    // If selectedCourse is provided, populate the form for update
    if (selectedCourse) {
      setTitle(selectedCourse.title);
      setPrice(selectedCourse.price);
      setImage(selectedCourse.image);
      setIsUpdating(true);
    }
  }, [selectedCourse]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
        console.log({ title, price, image });
      if (isUpdating) {
        // If updating, use the updateCourse function
        await axios.put(`http://localhost:5000/courses/${selectedCourse._id}`, { title, price, image });
      } else {
        // If creating, use the createCourse function
        await axios.post('http://localhost:5000/courses', { title, price, image });
      }

      // Refresh the course list after submission
      onFormClose();
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <div>
      <h2>{isUpdating ? 'Update Course' : 'Create Course'}</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
        </label>
        <label>
          Price:
          <input type="text" value={price} onChange={(e) => setPrice(e.target.value)} required />
        </label>
        <label>
          Image URL:
          <input type="text" value={image} onChange={(e) => setImage(e.target.value)} required />
        </label>
        <button type="submit">{isUpdating ? 'Update' : 'Create'}</button>
        <button type="button" onClick={onFormClose}>Cancel</button>
      </form>
    </div>
  );
};

export default CourseForm;
